mapper.select:
    select

	from olacircle 


		and type = #{type,jdbcType=INTEGER}


        and userId = #{userId,jdbcType=VARCHAR}

    order by createTime desc

select o.* from olacircle o,comment c where o.id = c.postId
and c.userId = #{userId,jdbcType=VARCHAR} and o.userId != #{userId,jdbcType=VARCHAR}
group by c.postId order by createTime desc

    select

	from olacircle 
	where createTime < #{createTime,jdbcType=TIMESTAMP} 

		and type = #{type,jdbcType=INTEGER}


        and isPublic = 1 


        and (isPublic = 1 or (isPublic = 0 and (userId = #{userId,jdbcType=VARCHAR} or assignUser = #{userId,jdbcType=VARCHAR}) ))

	order by createTime desc limit #{pageCount,jdbcType=INTEGER}

select 

from olacircle
where userid = #{userid,jdbcType=VARCHAR}
 and courseId = #{courseId,jdbcType=VARCHAR}
