mapper.select:
    select

	from subjectLog sl ,course_subject cs, `subject` t
	where sl.sid = cs.sid and t.id = sl.sid
		and sl.isright ='0'
		and sl.userid =#{userid,jdbcType=VARCHAR}
		and cs.cid = #{cid,jdbcType=INTEGER}

    select

	from subjectExamLog sl ,exam_subject es, `subject` t
	where sl.sid = es.sid and t.id = sl.sid
		and sl.isright ='0'
		and sl.userid =#{userId,jdbcType=VARCHAR}
		and es.eid = #{examId,jdbcType=INTEGER}
